        // });

        this.render()
    }

    render() {
        return (
            // <div>{this.cardInfo}</div>

                <div className="grid page">
                { this.state.cardInfo.map(card, index => {
                    console.log(card.title);
                    return (
                        <MyCard  type={card.type} desc={card.desc} imageId={card.image_id} key={index}/>
                    )  
